-- SCHEMA

CREATE SCHEMA plan;
SET SEARCH_PATH = plan;

-- CREATE TABLES


CREATE TABLE plan.currency (
                               id int8,
                               xuid text NOT NULL,
                               "name" text NOT NULL,
                               description text NULL,
                               "attributes" jsonb NULL,
                               CONSTRAINT currency_id_pk PRIMARY KEY (id),
                               CONSTRAINT currency_name_uidx UNIQUE (name),
                               CONSTRAINT currency_xuid_uidx UNIQUE (xuid)
);

CREATE TABLE plan.fx_rate (
                              id int8 GENERATED ALWAYS AS IDENTITY( INCREMENT BY 1 MINVALUE 1 MAXVALUE 9223372036854775807 START 1 CACHE 1 NO CYCLE) NOT NULL,
                              currency_id int8 NOT NULL,
                              to_currency_id int8 NOT NULL,
                              "date" date NOT NULL,
                              rate float8 NOT NULL,
                              CONSTRAINT fx_rate_id_pk PRIMARY KEY (id),
                              CONSTRAINT fx_rate_uidx UNIQUE (currency_id, to_currency_id, date)
);

ALTER TABLE plan.fx_rate ADD CONSTRAINT fx_rate_currency_id_fk FOREIGN KEY (currency_id) REFERENCES plan.currency(id) ON DELETE CASCADE;
ALTER TABLE plan.fx_rate ADD CONSTRAINT fx_rate_to_currency_id_fk FOREIGN KEY (to_currency_id) REFERENCES plan.currency(id) ON DELETE CASCADE;

CREATE TABLE plan.data_date (
    "date" DATE NOT NULL
);

